<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd"><html lang="en"><head><meta http-equiv="Content-Type" content="text/html;charset=UTF-8" /><title>Document</title></head><link rel="stylesheet" href="__PUBLIC__/Css/table.css" /><body><div id="menuContent" class="menuContent" style="display:none; position: absolute;background-color: #5FC6DA"><ul id="tree" class="ztree" style="margin-top:0; width:160px;"></ul></div><table width="580" cellspacing="0" cellPadding="0" class="listTable"><thead><tr><td>id</td><td>图片</td><td>库存</td></tr></thead><tbody><?php if(is_array($product)): $i = 0; $__LIST__ = $product;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$p): $mod = ($i % 2 );++$i;?><tr id="<?php echo ($p["id"]); ?>"><td><?php echo ($p["id"]); ?></td><td><?php echo ($p["name"]); ?></td><td><?php echo ($p["inventory"]); ?></td></tr><?php endforeach; endif; else: echo "" ;endif; if(empty($product)): ?><tr><td colspan="3">没有数据</td></tr><?php endif; ?></tbody><tfoot><tr><td colspan="3"><a href="#" id="startSort" class="easyui-linkbutton"  data-options="iconCls:'icon-ok'">开始排序</a>&nbsp;<a href="#" class="easyui-linkbutton"  data-options="iconCls:'icon-save',disabled:true" id="stopSort">结束保存</a>&nbsp;</td></tr></tfoot></table></body></html>