<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en"><head><meta http-equiv="Content-Type" content="text/html;charset=UTF-8" /><title>Document</title></head><body><form action="<?php echo U('Product/attrProcess');?>" method="post">	    商品名称:<?php echo ($product["name"]); ?><input type="hidden" name="channel_id" value="<?php echo ($product["cid"]); ?>" /><br/>	    商品尺码:<input type="text" name="size" id="" /><br/>	    商品库存: <input type="text" name="inventory" id="" /><br/><input type="submit" value="提交" /></form><?php if(!empty($attr)): ?><table width="380" cellspacing="0" cellPadding="0" class="listTable"><thead><tr><td>尺码</td><td>库存</td><td>操作</td></tr></thead><tbody><?php if(is_array($attr)): $i = 0; $__LIST__ = $attr;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$a): $mod = ($i % 2 );++$i;?><tr><td><?php echo ($a["size"]); ?></td><td><?php echo ($a["inventory"]); ?></td><td><a href="<?php echo U('Product/attrUpdate' , array('pid' => $a['id']) ) ;?>" class="attr_update">更新</a>&nbsp;&nbsp;<a href="#" title="<?php echo ($a["id"]); ?>" class="attr_del">删除</a></td></tr><?php endforeach; endif; else: echo "" ;endif; ?></tbody><tfoot></tfoot></table><?php endif; if(empty($attr)): ?><div>没有尺码数据</div><?php endif; ?></body></html>